function handleMeal(mealName) {
    var now       = msg.currentTime;
    var mealTime  = msg[mealName];
    var dog       = global.get(msg.dogID) || {};
    dog.mealStates  = dog.mealStates || {};

    // Check if it’s exactly meal time
    if (
        now.getHours()   === mealTime.getHours() &&
        now.getMinutes() === mealTime.getMinutes()
    ) {
        // Already fed this meal?
        if (dog.mealStates[mealName]) {
            return null;
        }

        // Build payload
        var amount = dog[mealName + 'Amount'];
        msg.payload = {
            dogName: msg.dogID,
            meal:     mealName,
            amount:   amount,
            time:     mealTime
        };

        // Mark as fed for this meal
        dog.mealStates[mealName] = true;
        global.set(msg.dogID, dog);


        var foodRemaining = global.get("foodRemaining") || 0;
        foodRemaining -= amount;
        global.set("foodRemaining", foodRemaining);


        return msg;
    }

    // Reset meal state after the meal window passes
    if (
        dog.mealStates[mealName] &&
        (
            now.getDay()     > mealTime.getDay()   ||
            now.getHours()   > mealTime.getHours() ||
            now.getMinutes() > mealTime.getMinutes()
        )
    ) {
        dog.mealStates[mealName] = false;
        global.set(msg.dogID, dog);
    }

    return null;
}

// Try each meal in order
return handleMeal("breakfast")
    || handleMeal("lunch")
    || handleMeal("dinner");

